    public void imgpers(){
        pess.setIdPessoa(InfoP.getId());
        pp.setIdPessoa(pess);
        try {
            id = ppDAO.AcharPersonagem(pp);
        } catch (Exception ex) {
            Logger.getLogger(TelaEscolhaPersonagem.class.getName()).log(Level.SEVERE, null, ex);
        }
        if(id!=-1){
            try {
                    pers = persDAO.getPersonagemPorID(id);
                } catch (Exception ex) {
                    Logger.getLogger(TelaEscolhaPersonagem.class.getName()).log(Level.SEVERE, null, ex);
                }

            per.setIcon(new javax.swing.ImageIcon(getClass().getResource(pers.getGif())));
        }
    }
