    private void formWindowActivated(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowActivated
        // TODO add your handling code here:
        pp.setIdPessoa(pess);
        try {
            if(ppDAO.AcharPersonagem(pp)==-1){
                ape.setText("Pressione S para jogar");
            }else{
                ape.setText("Aperte no personagem ou S para iniciar o jogo");
            }
        } catch (Exception ex) {
            Logger.getLogger(TelaPrincipal2.class.getName()).log(Level.SEVERE, null, ex);
        }
        setExtendedState(MAXIMIZED_BOTH);
        try {
            pess = pessDAO.getPessoaPorID(InfoP.getId());
        } catch (Exception ex) {
            Logger.getLogger(TelaPrincipal2.class.getName()).log(Level.SEVERE, null, ex);
        }
        moedas.setText("<html> <font size='6' face='Baskerville Old Face'>  "+pess.getMoedas());
        Pontuacoes();
        info();
    }//GEN-LAST:event_formWindowActivated

