    public void Jogar(){
     try {
            pp.setIdPessoa(pess);
            if(ppDAO.AcharPersonagem(pp)==-1){
                JOptionPane.showMessageDialog(null, "Compre ou selecione um personagem");
            }else{
                TelaJogo abrir = new TelaJogo();
                abrir.setVisible(true);
                dispose();
            }
            
        } catch (Exception ex) {
            Logger.getLogger(TelaPrincipal2.class.getName()).log(Level.SEVERE, null, ex);
        }
     
     
    }
    
