    public void Pontuacoes(){
        try {
            pess = pessDAO.getPessoaPorID(cod);     
//            pess = pessDAO.getPessoaPorID(1);
            MP.setText(""+pess.getPontuacao());
            m10.setModel(pessDAO.getMd10());
            
            criarlabelsdepontuacao();
            
        } catch (Exception ex) {
            Logger.getLogger(TelaPrincipal2.class.getName()).log(Level.SEVERE, null, ex);
        } 
    }
    public void criarlabelsdepontuacao(){
        int y = 200;
        for(int i=0; i<m10.getRowCount() ; i++){
//            JOptionPane.showMessageDialog(null, i);
//            System.out.println(i);
            JLabel novonome = new JLabel();
            novonome.setText("<html> <font style='font-size:18px' face='Baskerville Old Face'>"+m10.getValueAt(i, 0)+"</font></html>");
            novonome.setRequestFocusEnabled(false);
            novonome.setBounds(1075, y, 200, 35);
            
            Nomes.add(novonome);
            for (JLabel NomeAtual : Nomes) {
                painel.add(NomeAtual);
            }
            
            JLabel novapontuacao = new JLabel();
            novapontuacao.setText("<html> <font style='font-size:18px' face='Baskerville Old Face'>"+m10.getValueAt(i, 1)+"</font></html>");
            novapontuacao.setRequestFocusEnabled(false);
            novapontuacao.setBounds(1250, y, 200, 35);
            Pontuacao.add(novapontuacao);
            for (JLabel PontuacaoAtual : Pontuacao) {
                painel.add(PontuacaoAtual);
            }
            y+=50;
        }   
    }

